import React, { useState, useEffect } from 'react';
import FilmForm from './FilmForm';
import FilmList from './FilmList';
import Header from './Header';
import Footer from './Footer';
import './App.css';



function App() {
  const [films, setFilms] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editableFilm, setEditableFilm] = useState(null);

  const fetchFilms = () => {
    fetch('http://localhost:3000/films')
      .then(response => response.json())
      .then(data => {
        setFilms(data);
      })
      .catch(error => console.error('Error:', error));
  };


  // this function only add the film to the frontend 
  const addFilm = (filmData) => {
    // Assuming filmData has an id, if not, you can generate a unique id
    const newFilm = {
      id: Date.now(), // or any other unique id generation logic
      title: filmData.title,
      description: filmData.description,
      rating: filmData.rating,
      // Assuming you handle the thumbnail and video file as URLs or data URIs
      thumbnail: filmData.thumbnail, // Update this based on how you handle files
      video: filmData.file // Update this based on how you handle files
    };

    setFilms([...films, newFilm]);
    setShowForm(false);
  };




  // function below add the film to databas in backend 
  // const addFilm = (filmData) => {
  //   const formData = new FormData();
  //   formData.append('title', filmData.title);
  //   formData.append('description', filmData.description);
  //   formData.append('rating', filmData.rating);
  //   formData.append('thumbnail', filmData.thumbnail);
  //   formData.append('video', filmData.file);

  //   fetch('http://localhost:3000/films', {
  //     method: 'POST',
  //     body: formData,
  //   })
  //     .then(response => response.json())
  //     .then(() => {
  //       fetchFilms();
  //       setShowForm(false);
  //     })
  //     .catch((error) => {
  //       console.error('Error:', error);
  //     });
  // };

  const handleCancel = () => {
    setShowForm(false);
  };


  const onCloseFormAndRefresh = () => {
    setShowForm(false);
    setTimeout(() => {
      window.location.reload();
    }, 100);
  };


  useEffect(() => {
    fetchFilms();
  }, []);

  const deleteFilm = (filmId) => {
    fetch(`http://localhost:3000/films/${filmId}`, {
      method: 'DELETE',
    })
      .then(response => {
        if (response.ok) {
          fetchFilms();
        } else {
          console.error('Failed to delete the film');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleEditFilm = (updatedFilm) => {
    fetch(`http://localhost:3000/films/${updatedFilm.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedFilm),
    })
      .then(response => {
        if (response.ok) {
          fetchFilms();
        } else {
          console.error('Failed to update the film');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });

    setShowForm(false);
    setEditableFilm(null);
  };

  const startEditFilm = (film) => {
    setEditableFilm(film);
    setShowForm(true);
  };

  return (
    <div className="App">
      <Header /> { }
      <h1>Filmdatabas</h1>
      <button onClick={() => { setShowForm(true); setEditableFilm(null); }}>Add New Film</button>
      {showForm && (
        <FilmForm
          filmToEdit={editableFilm}
          onAddFilm={addFilm}
          onEditFilm={handleEditFilm}
          onCancel={handleCancel}
          onCloseFormAndRefresh={onCloseFormAndRefresh}
        />
      )}
      <FilmList
        films={films}
        onDelete={deleteFilm}
        onEdit={startEditFilm}
      />
      <Footer /> { }
    </div>
  );
}

export default App;
